import { AfterViewInit, Component } from '@angular/core';
import { Subscription } from 'rxjs';
import { WorkoutService } from '../workout.service';
import { Exerceises } from './exercise';

@Component({
  selector: 'app-workout-edit',
  templateUrl: './workout-edit.component.html',
  styleUrls: ['./workout-edit.component.css']
})
export class WorkoutEditComponent implements AfterViewInit {

  /**
   *
   */
  pageTitle = 'Exercise List';
  imageWidth = 50;
  imageMargin = 2;
  showImage = false;
  errorMessage = '';



  private sub: Subscription;


  _listFilter = '';
  get listFilter(): string {
    return this._listFilter;
  }
  set listFilter(value: string) {
    this._listFilter = value;
    this.filteredWorkouts = this.listFilter ? this.performFilter(this.listFilter) : this.Workouts;
  }

  filteredWorkouts: Exerceises[] = [];
  Workouts: Exerceises[] = [];

  constructor(private workoutService: WorkoutService,
    private fb: FormBuilder,
              private route: ActivatedRoute,
              private router: Router) { }

  performFilter(filterBy: string): Exerceises[] {
    filterBy = filterBy.toLocaleLowerCase();
    return this.Workouts.filter((Workout: Exerceises) =>
      Workout.Category.toLocaleLowerCase().indexOf(filterBy) !== -1);
  }


  toggleImage(): void {
    this.showImage = !this.showImage;
  }

  ngOnInit(): void {

    this.sub = this.route.paramMap.subscribe(
      params => {
        const id = +params.get('id');
        this.getProduct(id);
      }

    // this.workoutService.getExercises().subscribe({
    //   next: Workouts => {
    //     this.Ex = Workouts;
    //     this.filteredWorkouts = this.Workouts;
    //   },
    //   error: err => this.errorMessage = err
    // });
  }


}
