import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { WorkoutService } from '../workout.service';
import { Exerceise } from './exercise';

@Component({

  templateUrl: './workout-details.component.html',
  styleUrls: ['./workout-details.component.css']
})
export class WorkoutDetailsComponent {


  pageTitle = 'Product Detail';
  errorMessage = '';
  exercise: Exerceise[] = []
  exercisedGrouped: Exerceise[] =[]


  constructor(private route: ActivatedRoute,
              private router: Router,
              private workoutService: WorkoutService) {
  }

  ngOnInit(): void {
    const param = this.route.snapshot.paramMap.get('id');
    if (param) {
      const id = +param;
      this.getExercise(id);
    }


  }

  getExercise(id: number): void {
    this.workoutService.getExercises(id).subscribe({
      next: exercise => this.exercise = exercise,
      error: err => this.errorMessage = err
    });
  }

  transformArray(array: Array<any>, field) {
    if (array) {
      const groupedObj = array.reduce((prev, cur) => {
        if (!prev[cur[field]]) {
          prev[cur[field]] = [cur];
        } else {
          prev[cur[field]].push(cur);
        }
        return prev;
      }, {});
      return Object.keys(groupedObj).map(key => ({ key, value: groupedObj[key] }));
    }
    return [];
  }



  onBack(): void {
    this.router.navigate(['/workouts']);
  }

}
