import { Component, OnInit } from '@angular/core';
import { WorkoutService } from '../workout.service';
import { Workout } from './workout';

@Component({
  selector: 'app-workout',
  templateUrl: './workout.component.html',
  styleUrls: ['./workout.component.css']
})
export class WorkoutComponent implements OnInit {


  pageTitle = 'Workout List';
  imageWidth = 50;
  imageMargin = 2;
  showImage = false;
  errorMessage = '';

  _listFilter = '';
  get listFilter(): string {
    return this._listFilter;
  }
  set listFilter(value: string) {
    this._listFilter = value;
    this.filteredWorkouts = this.listFilter ? this.performFilter(this.listFilter) : this.Workouts;
  }

  filteredWorkouts: Workout[] = [];
  Workouts: Workout[] = [];

  constructor(private workoutService: WorkoutService) { }

  performFilter(filterBy: string): Workout[] {
    filterBy = filterBy.toLocaleLowerCase();
    return this.Workouts.filter((Workout: Workout) =>
      Workout.location.toLocaleLowerCase().indexOf(filterBy) !== -1);
  }


  toggleImage(): void {
    this.showImage = !this.showImage;
  }

  ngOnInit(): void {
    this.workoutService.getWorkouts().subscribe({
      next: Workouts => {
        this.Workouts = Workouts;
        this.filteredWorkouts = this.Workouts;
      },
      error: err => this.errorMessage = err
    });
  }

}
