import { Component, Pipe, PipeTransform } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { from, Subscription } from 'rxjs';
import { WorkoutService } from '../workout.service';
import { Exerceise } from './exercise';

@Component({

  templateUrl: './workout-details.component.html',
  styleUrls: ['./workout-details.component.css']
})
@Pipe({

})
export class WorkoutDetailsComponent implements PipeTransform  {


  pageTitle = 'Product Detail';
  errorMessage = '';
  exercise: Exerceise[] = []
  exercisedGrouped: Exerceise[] =[]


  constructor(private route: ActivatedRoute,
              private router: Router,
              private workoutService: WorkoutService) {
  }

  ngOnInit(): void {
    const param = this.route.snapshot.paramMap.get('id');
    if (param) {
      const id = +param;
      this.getExercise(id);
    }


  }

  getExercise(id: number): void {
    this.workoutService.getExercises(id).subscribe({
      next: exercise => this.exercise = exercise,
      error: err => this.errorMessage = err
    });
  }

  transform(collection: any[], property: string): any[] {
    // prevents the application from breaking if the array of objects doesn't exist yet


    const groupedCollection = collection.reduce((previous, current)=> {
        if(!previous[current[property]]) {
            previous[current[property]] = [current];
        } else {
            previous[current[property]].push(current);
        }

        return previous;
    }, {});

    // this will return an array of objects, each object containing a group of objects
    return Object.keys(groupedCollection).map(key => ({ key, value: groupedCollection[key] }));
  }



  onBack(): void {
    this.router.navigate(['/workouts']);
  }

}
