import { AfterViewInit, Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { WorkoutService } from '../workout.service';
import { Exerceises } from './exercise';
import { Workout } from './workout';

@Component({
  selector: 'app-workout-edit',
  templateUrl: './workout-edit.component.html',
  styleUrls: ['./workout-edit.component.css']
})
export class WorkoutEditComponent  {

  /**
   *
   */



  private sub: Subscription;

  pageTitle = 'Exercise Edit';
  errorMessage: string | undefined;
  exerciseForm: FormGroup;

  exercise: Exerceises | undefined;



  _listFilter = '';
  get listFilter(): string {
    return this._listFilter;
  }
  set listFilter(value: string) {
    this._listFilter = value;
    this.filteredWorkouts = this.listFilter ? this.performFilter(this.listFilter) : this.Workouts;
  }

  filteredWorkouts: Exerceises[] = [];
  Workouts: Exerceises[] = [];

  constructor(private workoutService: WorkoutService,
    private fb: FormBuilder,
              private route: ActivatedRoute,
              private router: Router) { }

  performFilter(filterBy: string): Exerceises[] {
    filterBy = filterBy.toLocaleLowerCase();
    return this.Workouts.filter((Workout: Exerceises) =>
      Workout.Category.toLocaleLowerCase().indexOf(filterBy) !== -1);
  }




  ngOnInit(): void {

    this.sub = this.route.paramMap.subscribe(
      params => {
        let id = +params.get('id');
        this.getExercse(id);
      }
    }

    getExercse(id: number): void {
      this.workoutService.getExercises(id)
        .subscribe({
          next: (workout: WorkoutService) => this.displayProduct(workout),
          error: err => this.errorMessage = err
        });
    }


    displayExercise(Exercise: Workout): void {
      if (this.exerciseForm) {
        this.exerciseForm.reset();
      }
      this.product = product;

      if (this.product.id === 0) {
        this.pageTitle = 'Add Product';
      } else {
        this.pageTitle = `Edit Product: ${this.product.productName}`;
      }

      // Update the data on the form
      this.exerciseForm.patchValue({
        productName: this.product.productName,
        productCode: this.product.productCode,
        starRating: this.product.starRating,
        description: this.product.description
      });
      this.exerciseForm.setControl('tags', this.fb.array(this.product.tags || []));
    }

    // this.workoutService.getExercises().subscribe({
    //   next: Workouts => {
    //     this.Ex = Workouts;
    //     this.filteredWorkouts = this.Workouts;
    //   },
    //   error: err => this.errorMessage = err
    // });




}
