import { AfterViewInit, Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { WorkoutService } from '../workout.service';
import { Exerceise } from './exercise';

import { Workout } from './workout';

@Component({
  selector: 'app-workout-edit',
  templateUrl: './workout-edit.component.html',
  styleUrls: ['./workout-edit.component.css']
})
export class WorkoutEditComponent  {


  pageTitle = 'exercise Edit';
  errorMessage: string;
  exerciseForm: FormGroup;

  exercise: Exerceise;
  private sub: Subscription;

  // Use with the generic validation message class
  displayMessage: { [key: string]: string } = {};
  private validationMessages: { [key: string]: { [key: string]: string } };
  // private genericValidator: GenericValidator;



  constructor(private fb: FormBuilder,
              private route: ActivatedRoute,
              private router: Router,
              private workoutService: WorkoutService) {

    // Defines all of the validation messages for the form.
    // These could instead be retrieved from a file or database.
    this.validationMessages = {
      exerciseName: {
        required: 'exercise name is required.',
        minlength: 'exercise name must be at least three characters.',
        maxlength: 'exercise name cannot exceed 50 characters.'
      },
      exerciseCode: {
        required: 'exercise code is required.'
      },
      starRating: {
        range: 'Rate the exercise between 1 (lowest) and 5 (highest).'
      }
    };

    // Define an instance of the validator for use with this form,
    // passing in this form's set of validation messages.
    // this.genericValidator = new GenericValidator(this.validationMessages);
  }

  ngOnInit(): void {
    this.exerciseForm = this.fb.group({
      exerciseName: ['', [Validators.required,
                         Validators.minLength(3),
                         Validators.maxLength(50)]],
      exerciseCode: ['', Validators.required],
      starRating: ['', NumberValidators.range(1, 5)],
      tags: this.fb.array([]),
      description: ''
    });

    // Read the exercise Id from the route parameter
    this.sub = this.route.paramMap.subscribe(
      params => {
        const id = +params.get('id');
        this.getexercise(id);
      }
    );
  }

  ngOnDestroy(): void {
    this.sub.unsubscribe();
  }

  // ngAfterViewInit(): void {
  //   // Watch for the blur event from any input element on the form.
  //   // This is required because the valueChanges does not provide notification on blur
  //   const controlBlurs: Observable<any>[] = this.formInputElements
  //     .map((formControl: ElementRef) => fromEvent(formControl.nativeElement, 'blur'));

  //   // Merge the blur event observable with the valueChanges observable
  //   // so we only need to subscribe once.
  //   merge(this.exerciseForm.valueChanges, ...controlBlurs).pipe(
  //     debounceTime(800)
  //   ).subscribe(value => {
  //     this.displayMessage = this.genericValidator.processMessages(this.exerciseForm);
  //   });
  // }



  getexercise(id: number): void {
    this.workoutService.getExercises(id)
      .subscribe({
        next: (exercise: Exerceise) => this.displayexercise(exercise),
        error: err => this.errorMessage = err
      });
  }

  displayexercise(exercise: Exerceise): void {
    if (this.exerciseForm) {
      this.exerciseForm.reset();
    }
    this.exercise = exercise;

    if (this.exercise.id === 0) {
      this.pageTitle = 'Add exercise';
    } else {
      this.pageTitle = 'Edit exercise:';
    }

    // Update the data on the form
    this.exerciseForm.patchValue({
      category: this.exercise.category,
      series: this.exercise.series,
      starRating: this.exercise.weight,

    });
    this.exerciseForm.setControl('tags', this.fb.array(this.exercise.tags || []));
  }

  deleteexercise(): void {
    if (this.exercise.id === 0) {
      // Don't delete, it was never saved.
      this.onSaveComplete();
    } else {
      if (confirm(`Really delete the exercise: ${this.exercise.exerciseName}?`)) {
        this.exerciseService.deleteexercise(this.exercise.id)
          .subscribe({
            next: () => this.onSaveComplete(),
            error: err => this.errorMessage = err
          });
      }
    }
  }

  saveexercise(): void {
    if (this.exerciseForm.valid) {
      if (this.exerciseForm.dirty) {
        const p = { ...this.exercise, ...this.exerciseForm.value };

        if (p.id === 0) {
          this.exerciseService.createexercise(p)
            .subscribe({
              next: () => this.onSaveComplete(),
              error: err => this.errorMessage = err
            });
        } else {
          this.exerciseService.updateexercise(p)
            .subscribe({
              next: () => this.onSaveComplete(),
              error: err => this.errorMessage = err
            });
        }
      } else {
        this.onSaveComplete();
      }
    } else {
      this.errorMessage = 'Please correct the validation errors.';
    }
  }

  onSaveComplete(): void {
    // Reset the form to clear the flags
    this.exerciseForm.reset();
    this.router.navigate(['/exercises']);
  }



}
